import React from "react";


function Sprite(props){
const c = canvas.getContext('2d');
    function draw(){
        c.drawImage(
            props.image,
            props.frames.val * props.width,
            0,
            props.image.width / props.frames.max,
            props.image.height,
            props.position.x,
            props.position.y,
            props.image.width / props.frames.max,
            props.image.height
        )
        if (!props.moving) return
        if (props.frames.max > 1) {
            props.frames.elapsed++
        }

        if (props.frames.elapsed % 5 === 0){
            if (props.frames.val < props.frames.max - 1) props.frames.val++
            else props.frames.val = 0
        }

    };
    return(
        <>
        {draw()}
        </>
    )
