// npm install && json-server -p 3001 --watch db.json && npm start

// import logo from './logo.svg';
import './App.css';
import React, {useState} from "react";


function App() {
  let [name, setName] = useState('')
  let [showSplash, setShowSplash] = useState(true)

  function addName (event, newName) {
    event.preventDefault()

    const baseUrl = "http://localhost:3001/"
    const namesUrl = baseUrl + "creatureName/"

    let postRequest = {
      method: 'POST',
      headers: {
        'Content-type': 'application/json',
        'Accept': 'application/json'
      },
      body: JSON.stringify({
        "name":newName
      })
    }
    fetch(namesUrl, postRequest)
    .then( r => r.json())
    .then( response => {
      setName(newName)
      setShowSplash(false)
    })
  }

  return (
    <div className="App">
      <header>
        <h1>{name}</h1>
      </header>
      {showSplash && <Splash
          name = {name}
          addName = {addName}
        />
      }
      {!showSplash && <GucciPage />}
    </div>
  );
}

export default App;
