// // npm install && rails server -p 3000 && json-server -p 3001 --watch db.json && npm start

// // sudo service postgresql start

import './App.css';
import React, {useState, useEffect} from "react";
import Navbar from "./components/Navbar";
import Splash from "./components/Splash";
import LobbyPage from "./components/LobbyPage";
import MyAccount from "./components/MyAccount";
import SignIn from "./components/SignIn";
import SignUp from "./components/SignUp";
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';

function App() {
  let [username, setName] = useState('')
  let [showSplash, setShowSplash] = useState(true)
  let [currentUser, setCurrentUser] = useState(true)
  const baseUrl = "http://localhost:3000/"
  const loginUrl = baseUrl + "login"
  const autoLoginUrl = baseUrl + "auto_login"

  useEffect( ()=> {
    console.log(localStorage.uid)
    if ( localStorage.uid )
      fetch(autoLoginUrl, {headers: {
        'content-type': 'application/json',
        'auth-token': localStorage.uid
      }})
        .then( r => r.json())
        .then(console.log)
        // .then(setCurrentUser)
      else
      console.log("No user info found.")
  }, [])

  const login =()=> {
    fetch( loginUrl, {
      method: 'POST',
      headers: {
        'Content-type': 'application/json',
        Accept: 'application/json'
      },
      body: JSON.stringify()
    })
    .then( r => r.json())
    .then(user => {
      localStorage.uid = user.uid
      setCurrentUser(user.id)
    })
  }

  function addName (event, newName) {
    event.preventDefault()

    const baseUrl = "http://localhost:3000/"
    const namesUrl = baseUrl + "users"

      let postRequest = {
        method: 'POST',
        headers: {
          'Content-type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify({
          "test_user":newName
        })
      }
    fetch(namesUrl, postRequest)
    .then( r => r.json())
    .then( response => {
      console.log(response)
      setName(newName)
      setShowSplash(false)
    })
  }

  return (
    <div className="App">
      <header>
      <h3>Welcome {username}</h3>

      <Router>
        <Navbar />
        <Routes>
          <Route exact path="/" element={<Splash
            username = {username}
            addName = {addName}
          />
          } />
          <Route path="/my-account"  element={<MyAccount/>} />
          <Route path="/sign-in"  element={<SignIn/>} />
          <Route path="/sign-up"  element={<SignUp/>} />
          <Route path="/lobby"  element={<LobbyPage
            username = {username}
            addName = {addName}
          />
          } />
          <Route fallback={<p>Not found</p>} />
        </Routes>
      </Router>
      </header>
      {/* {showSplash && <Splash
          username = {username}
          addName = {addName}
        />}
      {!showSplash && <LobbyPage
        username = {username}
        addName = {addName}
      />} */}
    </div>
  );
}

export default App;
