import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

function SignIn(props) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const baseUrl = "http://localhost:3000/"
  const loginUrl = baseUrl + "login"
  const autoLoginUrl = baseUrl + "auto_login"


  useEffect( ()=> {
    console.log(localStorage.uid, "uid here")
    if ( localStorage.uid )
      fetch(autoLoginUrl, {headers: {
        'content-type': 'application/json',
        'auth-token': localStorage.uid
      }})
        .then( r => r.json())
        .then(console.log)
        // .then(props.setCurrentUser)
      else
      console.log("No user info found.")
  }, [])

  const login =()=> {
    fetch( loginUrl, {
      method: 'POST',
      headers: {
        'Content-type': 'application/json',
        Accept: 'application/json'
      },
      body: JSON.stringify({"email":email})
    })
    .then( r => r.json())
    .then(user => {
      localStorage.uid = user.uid
      props.setCurrentUser(user.id)
    })
  }

  const handleSubmit = async (event) => {
    event.preventDefault();
    console.log('submitting')
    try {
      login()
    } catch (error) {
      setError(error.message);
    }

  };

  return (
    <div>
    <form onSubmit={(event) => handleSubmit(event)}>
      {error && <p>{error}</p>}
      <label>
        Email:
        <input
          type="email"
          value={email}
          onChange={(event) => setEmail(event.target.value)}
        />
      </label>
      <br />
      <label>
        Password:
        <input
          type="password"
          value={password}
          onChange={(event) => setPassword(event.target.value)}
        />
      </label>
      <br />
      <button type="submit">Sign In</button>
      <p>Don't have an account? <Link className="link" to="/sign-up">Sign Up!</Link></p>
    </form>
    </div>
  );
}

export default SignIn;
