import React, {useState, useEffect} from "react";
import parse from 'html-react-parser';

export default function GameContainer(props){

    const baseUrl = "http://localhost:3001/"
    const itemsUrl = baseUrl + "items/"
    const usernamesUrl = baseUrl + "usernames/"
    useEffect(() => {

        let gameContainer = document.getElementById('GameContainer')
        console.log(gameContainer, "gamecontainer here")
        const script = document.createElement('script');

        script.src = "./Game.js";
        script.async = true;

        gameContainer.appendChild(script)

        // return () => {
        //   document.body.removeChild(script);
        // }
      }, []);

      function createMarkup() {
        return {__html: 'game.html'};
      }

      function gameHTML() {
        return <div dangerouslySetInnerHTML={createMarkup()} />;
      }
    const parse = require('html-react-parser');
    return(
    <div>

        <canvas id="GameContainer"><object type="text/html" data="game.html"></object>
</canvas>
        <iframe height="90vh" width="90vw" src="http://127.0.0.1:5500/src/components/game.html"></iframe>

        {parse('<h1>hello</h1>')}
        {parse('<div><script src="./classes.js"></script>hi</div>')}
        {parse('<script src="./Game.js"></script>')}
        {/* <script src="../../gameData/collisions.js"></script>
        <script src="classes.js"></script>
        <script src="Game.js"></script> */}
    </div>
    )

}
