import React, {useState, useEffect} from 'react';
import { useTimer } from 'use-timer';
import MainContainer from './MainContainer';
import GameContainer from './GameContainer';

const baseUrl = "http://localhost:3001/"
const itemsUrl = baseUrl + "items/"


function LobbyPage(props) {
    // const username = ""
    const [username, setName] = useState("")
    //*** Inventory START ***
    //set state for Inventory
    // const [showInventory, setShowInventory] = useState(false)

    //toggle true and false on click
    // const handleInventory = (e) => {
    //     setShowInventory((showInventory) => (!showInventory))
    // }

    const [itemsState, setItemsState] = useState([])
    useEffect(() => fetchItems(), [])

    function fetchItems() {
        fetch(itemsUrl)
        .then(resp => resp.json())
        .then(items => setItemsState(items))
    }
    //*** Inventory STOP ***


    //*** TIMER START ***/
    const { time, start, pause, reset, status } = useTimer();
    //this function gets called every second
    // useEffect(() => [time, console.log(time)])
    //*** TIMER STOP */

    function startBtn() {
        document.querySelector('.hideClick').classList.add('hidden');
        document.querySelector('.sidebar').classList.add('hidden');
        document.querySelector('.timer').classList.remove('hidden');
        document.querySelector('.main-content').classList.add('hidden');
      }

      function apeSelected() {
        document.querySelector('.filter-default').classList.add('grey');
      }

    return(
    <div>
        <div className='hideClick'>
            <button onClick={ () => {
            start();
            startBtn();
            }}>Start Match</button>
        </div>
        <p className='timer hidden'>{time}</p>
            <div className="sidebar">
                <div className="playersBox">
                    <strong>PLAYERS:</strong><br/>
                    <h1>{username}</h1>
                </div>
            </div>
            <div className="main-content">
                <h3 className="white">Choose an Ape</h3>
                <img onClick={ () => {
            {apeSelected()}
            }} className="apeSelector filter-default" src="images/AK-Monkeh.gif"></img>
                <img className="apeSelector filter-green" src="images/AK-Monkeh.gif"></img>
                <img className="apeSelector filter-purple" src="images/AK-Monkeh.gif"></img>
                <img className="apeSelector filter-cyan" src="images/AK-Monkeh.gif"></img>
            </div>
            <GameContainer
            username = {username}
            />
    </div>
)}

export default LobbyPage;
