import React, { useState, useEffect } from 'react';
import axios from 'axios'

function MyAccount() {
  const [items, setItems] = useState([]);
  const [equippedItems, setEquippedItems] = useState([]);
  // const baseUrl = "http://localhost:3000/";
  // const itemsUrl = baseUrl + "items";

  // useEffect(() => fetchItems(), [] )

  // function fetchItems() {
  //   fetch (itemsUrl)
  //   .then (r => r.json)
  //   .then (setItems)
  // }

  function itemFetching() {
    const [posts, setPosts] = useState([])
    useEffect(() => {
      axios.get()
      .then(res => {
        console.log(res)
      })
      .catch(err => {
        console.log(err)
      })
    })
  }

  const handleEquip = (item) => {
    setEquippedItems([...equippedItems, item]);
  };

  const handleUnequip = (item) => {
    setEquippedItems(equippedItems.filter((i) => i !== item));
  };

  return (
    <div>
      <h2>My Account</h2>
      <h3>Equipped Items:</h3>
      <ul>
        {equippedItems.map((item) => (
          <li key={item.id}>
            {item.name}
            <button onClick={() => handleUnequip(item)}>Unequip</button>
          </li>
        ))}
      </ul>
      <h3>All Items:</h3>
      <ul>
        {items.map((item) => (
          <li key={item.id}>
            {item.name}
            <button onClick={() => handleEquip(item)}>Equip</button>
          </li>
        ))}
      </ul>

    </div>
  );
}

export default MyAccount;
