        //         return currentThought.image
        //     })

        // }
        return(
            <div>

                <img className={props.hungerState <= 5 ? "" : "isHidden"} src="images\TB_Hungry.gif"/>
            </div>
        )
    }
    //SHOW BUBBLE FUNCTION STOP***//

    // <img className={props.moodState == 10 ? "" : "isRemoved"} src="images\TB_Heart.gif"/>
    // <img className={props.moodState <= 5 ? "" : "isRemoved"} src="images\TB_Unhappy.gif"/>

    //**CHARACTER SWAP FUNCTION START**
    function getMoodImage(moodNumber){
        // const promise = new Promise((resolve, reject) => {
            //resolve('Success!');

        return fetch(moodUrl)
        .then((response) => response.json())
        .then((moodData) => {
