    return (
    <div className="main-content">
        <div className="sidebar">
            <button onClick={props.feedGame}>Feed</button>
            <button onClick={props.petGame}>Pet</button>
            <div className="inventory">
                <strong>INVENTORY:</strong><br/>
                <p>(coming soon)</p>
            </div>
        </div>
        <div>
            <div className="thoughtBubble">
            {bubbleStatus()}
            </div>
            <div className="statusBar">
                <div className="status-icon">
                    <img src="images\Happyness.png"/>
                    <div className="happybars">
                        <div id="m10" className={props.moodState >= 10 ? "barFilled" : "bar"}>
                        </div>
                        <div id="m9" className={props.moodState >= 9 ? "barFilled" : "bar"}>
                        </div>
                        <div id="m8" className={props.moodState >= 8 ? "barFilled" : "bar"}>
                        </div>
                        <div id="m7" className={props.moodState >= 7 ? "barFilled" : "bar"}>
                        </div>
                        <div id="m6" className={props.moodState >= 6 ? "barFilled" : "bar"}>
                        </div>
                        <div id="m5" className={props.moodState >= 5 ? "barFilled" : "bar"}>
                        </div>
                        <div id="m4" className={props.moodState >= 4 ? "barFilled" : "bar"}>
                        </div>
                        <div id="m3" className={props.moodState >= 3 ? "barFilled" : "bar"}>
                        </div>
                        <div id="m2" className={props.moodState >= 2 ? "barFilled" : "bar"}>
                        </div>
                        <div id="m1" className={props.moodState >= 1 ? "barFilled" : "bar"}>

                        </div>
                    </div>
                    <img src="images\Burger.png"/>
                    <div className="hungerbars">
                        <div id="h10" className={props.hungerState >= 10 ? "barFilled" : "bar"}>
