// npm install && json-server -p 3001 --watch db.json && npm start

// import logo from './logo.svg';
import './App.css';
import React, {useState} from "react";
import {
  BrowserRouter as Router,
  Route,
  Link
} from 'react-router-dom';
import Navbar from "./components/Navbar";
import Splash from "./components/Splash";
import LobbyPage from "./components/LobbyPage";
import MyAccount from "./components/MyAccount";
import SignIn from "./components/SignIn";

function App() {
  let [username, setName] = useState('')
  let [showSplash, setShowSplash] = useState(true)

  function addName (event, newName) {
    event.preventDefault()

    const baseUrl = "http://localhost:3000/"
    const namesUrl = baseUrl + "usernames/"

    let postRequest = {
      method: 'POST',
      headers: {
        'Content-type': 'application/json',
        'Accept': 'application/json'
      },
      body: JSON.stringify({
        "username":newName
      })
    }
    fetch(namesUrl, postRequest)
    .then( r => r.json())
    .then( response => {
      setName(newName)
      setShowSplash(false)
    })
  }

  return (
    <Router>
    <div className="App">
      <header>
      <h1>{username}</h1>
      <Navbar/>
      </header>
      {showSplash && <Splash
          username = {username}
          addName = {addName}
        />
      }
      {!showSplash && <LobbyPage
        username = {username}
        addName = {addName}
      />}
    </div>

    <div>
      <nav>
        <ul>
          <li>
            <Link to="/">Home</Link>
          </li>
          <li>
            <Link to="/my-account">My Account</Link>
          </li>
          <li>
            <Link to="/sign-in">Sign In</Link>
          </li>
        </ul>
      </nav>

      {/* A <Switch> looks through its children <Route>s and
          renders the first one that matches the current URL. */}

        <Route exact path="/">
          <Splash />
        </Route>
        <Route path="/my-account">
          <MyAccount />
        </Route>
        <Route path="/sign-in">
          <SignIn />
        </Route>

    </div>
  </Router>

  );
}

export default App;
