// npm install && json-server -p 3001 --watch db.json && npm start

// import logo from './logo.svg';
import './App.css';
import React, {useState} from "react";
import Splash from "./components/Splash";
import LobbyPage from "./components/LobbyPage";

function App() {
  let [username, setName] = useState('')
  let [showSplash, setShowSplash] = useState(true)

  function addName (event, newName) {
    event.preventDefault()

    const baseUrl = "http://localhost:3000/"
    const namesUrl = baseUrl + "usernames/"

    let postRequest = {
      method: 'POST',
      headers: {
        'Content-type': 'application/json',
        'Accept': 'application/json'
      },
      body: JSON.stringify({
        "username":newName
      })
    }
    fetch(namesUrl, postRequest)
    .then( r => r.json())
    .then( response => {
      setName(newName)
      setShowSplash(false)
    })
  }

  return (
    <div className="App">

      <div className="main-content">
        <div className="sidebar">
            <div className="inventory">
                <strong>PLAYERS:</strong><br/>
                <h1>{username}</h1>
            </div>
        </div>
      </div>

      {showSplash && <Splash
          username = {username}
          addName = {addName}
        />
      }
      {!showSplash && <LobbyPage
        username = {username}
      />}
    </div>
  );
}

export default App;
