import React, {useState, useEffect} from "react";

export default function GameContainer(props){

    const baseUrl = "http://localhost:3001/"
    const thoughtUrl = baseUrl + "thoughtBubbles/"
    const moodUrl = baseUrl + "mood/"
    const hungerUrl = baseUrl + "hunger/"
    //Setter for showing the thought bubble container based on mood/hunger.
    const [showBubble, setShowBubble] = useState(false)
    const handleShowBubble = event => {
        //ShowBubble Container on event (<4 Bars)
        setShowBubble(true)
    }

    //Setter for generating the hunger bars based on mood/hunger
    const [isVisible, setIsVisible] = useState(true)
    const toggleShowBar = event => { setIsVisible(isVisible => !isVisible)}


    //Bubble should be hidden on hunger/mood 5+
    //SHOW BUBBLE FUNCTION START***//
    function bubbleStatus(){
        // function getThoughtImage(thoughtNumber){
        //     // const promise = new Promise((resolve, reject) => {
        //         //resolve('Success!');

        //     return fetch(moodUrl)
        //     .then((response) => response.json())
        //     .then((moodData) => {
        //         let currentThought = moodData.find( mood => mood.value == moodNumber )
        //         return currentThought.image
        //     })

        // }
        return(
            <div>

                <img className={props.hungerState <= 5 ? "" : "isHidden"} src="images\TB_Hungry.gif"/>
            </div>
        )
    }
    //SHOW BUBBLE FUNCTION STOP***//

    // <img className={props.moodState == 10 ? "" : "isRemoved"} src="images\TB_Heart.gif"/>
    // <img className={props.moodState <= 5 ? "" : "isRemoved"} src="images\TB_Unhappy.gif"/>






    return (
    <div className="main-content">
        <div className="sidebar">
            <button onClick={props.feedCreature}>Feed</button>
            <button onClick={props.petCreature}>Pet</button>
            <div className="inventory">
                <strong>INVENTORY:</strong><br/>
                <p>(coming soon)</p>
            </div>
        </div>
        <div>
            <div className="thoughtBubble">
            {bubbleStatus()}
            </div>
            <div className="statusBar">
                <div className="status-icon">
                    <img src="images\Happyness.png"/>
                    <div className="happybars">
                        <div id="m10" className={props.moodState >= 10 ? "barFilled" : "bar"}>
                        </div>
                        <div id="m9" className={props.moodState >= 9 ? "barFilled" : "bar"}>
                        </div>
                        <div id="m8" className={props.moodState >= 8 ? "barFilled" : "bar"}>
                        </div>
                        <div id="m7" className={props.moodState >= 7 ? "barFilled" : "bar"}>
                        </div>
                        <div id="m6" className={props.moodState >= 6 ? "barFilled" : "bar"}>
                        </div>
                        <div id="m5" className={props.moodState >= 5 ? "barFilled" : "bar"}>
                        </div>
                        <div id="m4" className={props.moodState >= 4 ? "barFilled" : "bar"}>
                        </div>
                        <div id="m3" className={props.moodState >= 3 ? "barFilled" : "bar"}>
                        </div>
                        <div id="m2" className={props.moodState >= 2 ? "barFilled" : "bar"}>
                        </div>
                        <div id="m1" className={props.moodState >= 1 ? "barFilled" : "bar"}>

                        </div>
                    </div>
                    <img src="images\Burger.png"/>
                    <div className="hungerbars">
                        <div id="h10" className={props.hungerState >= 10 ? "barFilled" : "bar"}>
                        </div>
                        <div id="h9" className={props.hungerState >= 9 ? "barFilled" : "bar"}>
                        </div>
                        <div id="h8" className={props.hungerState >= 8 ? "barFilled" : "bar"}>
                        </div>
                        <div id="h7" className={props.hungerState >= 7 ? "barFilled" : "bar"}>
                        </div>
                        <div id="h6" className={props.hungerState >= 6 ? "barFilled" : "bar"}>
                        </div>
                        <div id="h5" className={props.hungerState >= 5 ? "barFilled" : "bar"}>
                        </div>
                        <div id="h4" className={props.hungerState >= 4 ? "barFilled" : "bar"}>
                        </div>
                        <div id="h3" className={props.hungerState >= 3 ? "barFilled" : "bar"}>
                        </div>
                        <div id="h2" className={props.hungerState >= 2 ? "barFilled" : "bar"}>
                        </div>
                        <div id="h1" className={props.hungerState >= 1 ? "barFilled" : "bar"}>
                        </div>
                    </div>
                </div>
            </div>
            <div className="creature">
            {/* {character(props.moodState)} */}
            </div>
        </div>
    </div>
    )

}
