import React, { useState, useEffect } from 'react';
import { Link, Redirect  } from 'react-router-dom';

function SignIn(props) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const baseUrl = "http://localhost:3000/"
  const loginUrl = baseUrl + "login"
  const autoLoginUrl = baseUrl + "auto_login"

  // Handle auto login
  useEffect(() => {
    console.log(localStorage.uid, "uid here");
    if (localStorage.uid) {
      fetch(autoLoginUrl, {
        headers: {
          'Content-Type': 'application/json',
          'auth-token': localStorage.uid
        }
      })
      .then(r => r.json())
      .then(props.setCurrentUser)
      .catch(() => setError("An error occurred while trying to auto-login."));
    } else {
      console.log("No user info found.");
    }
  }, []);

  // Handle login
  const login = () => {
    fetch(loginUrl, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Accept: 'application/json'
      },
      body: JSON.stringify({ email, password })
    })
    .then(r => r.json())
    .then(({ uid, id }) => {
      localStorage.uid = uid;
      props.setCurrentUser(id);
    })
    .catch(() => setError("An error occurred while trying to login."));
  };


  let [redirect, setRedirect] = useState(false);
  let isSignedIn = props.isSignedIn();

  function handleClick() {
      if (isSignedIn) {
        setRedirect(true);
      }
   }

  if (redirect) {
     return <Redirect to="/my-account" />;
  }

  let setIsSignedIn = props.setIsSignedIn;

  // function toggleSignIn(id) {
  //   if (props.setCurrentUser(id) !== null) {
  //     setIsSignedIn = !setIsSignedIn;
  //   }
  // }

  const handleSubmit = (event) => {
    event.preventDefault();
    login();
    if (isSignedIn) {
      setRedirect(true);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        {error && <p>{error}</p>}
        <label>
          Email:
          <input
            type="email"
            value={email}
            onChange={(event) => setEmail(event.target.value)}
          />
        </label>
        <br />
        <label>
          Password:
          <input
            type="password"
            value={password}
            onChange={(event) => setPassword(event.target.value)}
          />
        </label>
        <br />
        <button type="submit" >Sign In</button>
        <p>Don't have an account? <Link className="link" to="/sign-up">Sign Up!</Link></p>
      </form>
    </div>
  );
}

export default SignIn;
